import { MdLocalPizza as icon } from 'react-icons/md';
import PriceInput from '../components/PriceInput';

export default {
    //computer name
    name: 'pizza',
    //visible title
    title: 'Pizzas',
    type: 'document',
    //can give icon any React comp
    icon: icon, 
    fields: [
        {
            name: 'name',
            title: 'Pizza Name',
            type: 'string',
            description: 'Name of the pizza'
        },
        {
            name: 'slug',
            title: 'slug',
            type: 'slug',
            options: {
                source: 'name',
                maxLength: 100,
            }
        },
        {
            name: 'image',
            title: 'Image',
            type: 'image',
            options: {
                hotspot: true,
            }
        },
        {
            name: 'price',
            title: 'Price',
            type: 'number',
            description: 'Price of the pizza in cents',
            validation: Rule => Rule.min(1000),
            //TODO - add custom component
            //overwriting sanity component
            inputComponent: PriceInput,
        },
        //relational content - one to many
        {
            name: 'toppings',
            title: 'Toppings',
            type: 'array',
            of: [{ type: 'reference', to: [{type: 'topping'}]}],
        }
    ],
    preview: {
        select: {
           title: 'name',
            media: 'image',
            topping0: 'toppings.0.name',
            topping1: 'toppings.1.name',
            topping2: 'toppings.2.name',
            topping3: 'toppings.3.name',
        },
        //destructure
        prepare: ({title, media, ...toppings}) => {
        //filter undefined toppings out
        const tops = Object.values(toppings).filter(Boolean)
        //return preview obj for pizza
        return {
            title,
            media,
            subtitle: Object.values(tops).join(', '),
        }
    }
    }
};

